#!/usr/bin/env node

const childProcess = require('child_process');
const path = require('path');

const electronPath = path.join(__dirname, 'dist', 'electron.exe');
const args = [path.join(__dirname, '..', '..')].concat(process.argv.slice(2));

const child = childProcess.spawn(electronPath, args, { stdio: 'inherit' });

child.on('close', (code) => {
    process.exit(code);
});